filterTime(data) {
  //this.dateTime  是需要转换的值
  let date = new Date(data);
  let y = date.getFullYear();
  let m = date.getMonth() + 1;
  m = m < 10 ? '0' + m : m;
  let d = date.getDate();
  d = d < 10 ? '0' + d : d;
  const time = y + '-' + m + '-' + d;
  return time;
},
